import React from 'react'
import { List, ListItemText, Paper, ListItem, ListItemSecondaryAction, IconButton, ButtonGroup, Button, makeStyles } from '@material-ui/core';
// import DeleteTwoToneIcon from '@material-ui/icons/DeleteTwoTone';
import { roundTo2DecimalPoint } from "../../utils";
import DeleteTwoToneIcon from '@mui/icons-material/DeleteTwoTone';
import SearchFoodItems from './SearchFoodItems';
import Order from './index';



const useStyles = makeStyles(theme => ({
    paperRoot: {
        margin: '15px 0px',
        '&:hover': {
            cursor: 'pointer'
        },
        '&:hover $deleteButton': {
            display: 'block'
        }
    },
    buttonGroup: {
        backgroundColor: '#E3E3E3',
        borderRadius: 8,
        '& .MuiButtonBase-root ': {
            border: 'none',
            minWidth: '25px',
            padding: '1px'
        },
        '& button:nth-child(2)': {
            fontSize: '1.2em',
            color: '#000'
        }
    },
    deleteButton: {
        display: 'none',
        '& .MuiButtonBase-root': {
            color: '#E81719'
        },
    },
    totalPerItem: {
        fontWeight: 'bolder',
        fontSize: '1.2em',
        margin: '0px 10px'
    }
}))

export default function OrderedFoodItems(props) {

    const {values,setValues } = props;

    let orderedFoodItems = values.orderDetails;

    const removeFoodItem = (index, id) => {
        debugger;
        let x = { ...values };
        x.orderDetails = x.orderDetails.filter((_, i) => i != index);
        if (id != 0)
            x.deletedOrderItemIds += id + ',';
        setValues({ ...x });
      }

    const updateQuantity = (idx, value) => {
        let x = { ...values };
        let foodItem = x.orderDetails[idx];
        if (foodItem.quantity + value > 0) {
            foodItem.quantity += value;
            setValues({ ...x });
        }
    }


  return (
   <List>
     {
    orderedFoodItems.map((item,idx)=>(
        <paper key={idx}>
            <ListItem>
                <ListItemText
                    primary={item.foodItemName}
                    primaryTypographyProps={{
                                    component: 'h1',
                                    style: {
                                        fontWeight: '500',
                                        fontSize: '1.2em'
                                    }
                                }}
                      secondary = {
                        <>
                        <ButtonGroup
                        size="small">
                            <Button
                            onClick={e => updateQuantity(idx, -1)}
                            >-</Button>
                            <Button
                            disabled
                            >{item.quantity}</Button>
                            <Button
                             onClick={e => updateQuantity(idx, +1)}
                            >+</Button>
                        </ButtonGroup>
                        
                        </>
                      }

                />
                <ListItemSecondaryAction>
                    <IconButton
                    disableRipple
                    onClick={ e => removeFoodItem(idx,item.orderDetailsId)}
                    >
                        <DeleteTwoToneIcon/>
                        
                    </IconButton>
                </ListItemSecondaryAction>
            </ListItem>

        </paper>
     ))

     }
   </List>
  )
}
