import React from 'react'
import OrderForm from './OrderForm';
import { useForm } from '../../hooks/useForm';
import { ButtonGroup, Grid, InputAdornment, makeStyles, Button as MuiButton } from '@material-ui/core'
import SearchFoodItems from './SearchFoodItems';
import OrderedFoodItems from './OrderedFoodItems';


const generateOrderNumber = () => Math.floor(10000 + Math.random() * 900000).toString();

const getFreshModelObject = () =>({
  orderMasterId :0,
  orderNumber :generateOrderNumber(),
  customerId : 0,
  pMethod : 'none',
  gTotal : 0,
  deletedOrderItemIds:'',
  orderDetails : []
})

export default function Order() {

  const {
    values,
    setValues,
    errors,
    setErrors,
    handleInputChange,
    resetFormControls
  } = useForm(getFreshModelObject)




  return (


<Grid container spacing={2}>

<Grid item xs={12}>
<OrderForm {...{ values, errors, handleInputChange }} />
            </Grid>

            <Grid item xs={6}>
                <SearchFoodItems
                 {...{
                     
                      values,
                      setValues
                }}       
                />
               
            </Grid>
            <Grid item xs={6}>
                <OrderedFoodItems
                    {...{      
                                 
                      values,
                      setValues
                      }}
                />
            </Grid>
        </Grid>

    
  )
}
